mapper.select:
select count(c.id) from contacts c left outer join customers s
on c.CUSTOMER_ID=s.id
where c.CUSTOMER_ID=#{id}

select *
from(
	select rownum rn,c.id,c.name,c.SEX,c.POSITION,c.TEL,c.MOBILE,c.MEMO,s.id as "customer.id"
	from contacts c left outer join customers s on c.CUSTOMER_ID=s.id
	where c.CUSTOMER_ID=#{id}
	order by c.id
	)

	where rn >= #{firstIndex} and rn < #{endIndex}

mapper.update:
update contacts set NAME=#{name},SEX=#{sex},POSITION=#{position},
TEL=#{tel},MOBILE=#{mobile},MEMO=#{memo}
where id=#{id}
